mapper.update:
  update bc_purchase_order
  set  
	  purchase_money =purchase_money+ #{balanceMoney,jdbcType=DECIMAL},
 	  balance_money =balance_money+ #{balanceMoney,jdbcType=DECIMAL},
    purchase_count =purchase_count+ #{purchaseCount,jdbcType=INTEGER} 
  where  purchaseorder_code = #{purchaseorderCode,jdbcType=VARCHAR}
