mapper.select:

select 

from oc_return_goods
where zid = #{zid,jdbcType=INTEGER}
  and uid = #{uid,jdbcType=CHAR}
  and return_code = #{returnCode,jdbcType=VARCHAR}

mapper.delete:

delete from oc_return_goods
where zid = #{zid,jdbcType=INTEGER}
  and uid = #{uid,jdbcType=CHAR}
  and return_code = #{returnCode,jdbcType=VARCHAR}
